import app from "../../../index";
import chaiHttp from "chai-http";
import chai, { expect } from "chai";

chai.use(chaiHttp);
const router = () => chai.request(app);
function registerAndLoginUser(callback: Function) {
  router()
    .post("/api/user/register")
    .send({
      name: "Test User",
      email: "testuserfortodo@gmail.com",
      password: "PasswordForUser",
    })
    .end((error, response) => {
      if (error) {
        return callback(error);
      }

      router()
        .post("/api/user/login")
        .send({
          email: "testuserfortodo@gmail.com",
          password: "PasswordForUser",
        })
        .end((error, response) => {
          if (error) {
            return callback(error);
          }

          const token = response.body.message.token;
          callback(null, token);
        });
    });
}

describe("Todo Test Cases", () => {

  let token="";
  let createdTodo = {};

  before(function (done) {
    registerAndLoginUser((error: any, retrievedToken: string) => {
      if (error) {
        return done(error);
      }
      token = retrievedToken;
      done();
    });
  });

  after(function (done) {
    router()
      .delete("/api/user/delete")
      .set("Authorization", `Bearer ${token}`)
      .end((error, response) => {
        done();
      });
  });

  it("Should not be able to add todo without login token", (done) => {
    router()
      .post("/api/todo/add")
      .send({
        title: "TodoTest1",
        description: "This is todo for testing",
      })
      .end((error, response) => {
        expect(response.body).to.be.an("object").that.has.property("status", false);
        done(error);
      });
  });

  it("Should not be able to get Todos without login Token", (done) => {
    router()
      .get("/api/todo/todos")
      .end((error, response) => {
        expect(response.body).to.be.a("object");
        expect(response.body).to.have.property("status", false);
        done(error);
      });
  });

  it("Should be able to add new todo", (done) => {
    router()
      .post("/api/todo/add")
      .set("Authorization", `Bearer ${token}`)
      .send({
        title: "TestTodo",
        description: "This is todo created in testing."
      })
      .end((error, response) => {
        expect(response.body).to.be.an("object");
        expect(response.body).to.have.property("status", true);
        expect(response.body).to.have.property("message").that.is.an("object");
        createdTodo=response.body.message;
        done(error);
      });
  });

  it("Should be able to update todo", (done) => {
    router()
      .patch("/api/todo/update")
      .set("Authorization", `Bearer ${token}`)
      .send({
        id: (createdTodo as any)._id,
        title: "Testing todo - updated",
        description: "This is updated todo created in testing.",
        completed: false,
      })
      .end((error, response) => {
        expect(response.body).to.be.an("object");
        expect(response.body).to.have.property("status", true);
        done(error);
      });
  });

  it("Should be able to mark todo as completed", (done) => {
    router()
      .patch("/api/todo/complete")
      .set("Authorization", `Bearer ${token}`)
      .send({
        id: (createdTodo as any)._id
      })
      .end((error, response) => {
        expect(response.body).to.be.an("object");
        expect(response.body).to.have.property("status", true);
        done(error);
      });
  });

  it("Should be able to mark todo as unCompleted", (done) => {
    router()
      .patch("/api/todo/unComplete")
      .set("Authorization", `Bearer ${token}`)
      .send({
        id: (createdTodo as any)._id
      })
      .end((error, response) => {
        expect(response.body).to.be.an("object");
        expect(response.body).to.have.property("status", true);
        done(error);
      });
  });

  it("Should be able to get All todos", (done) => {
    router()
      .get("/api/todo/todos")
      .set("Authorization", `Bearer ${token}`)
      .end((error, response) => {
        expect(response.body).to.be.an("object");
        expect(response.body).to.have.property("status", true);
        done(error);
      });
  });

  it("Should be able to delete todo", (done) => {
    router()
      .delete("/api/todo/delete")
      .set("Authorization", `Bearer ${token}`)
      .send({
        id: (createdTodo as any)._id,
      })
      .end((error, response) => {
        expect(response.body).to.be.an("object");
        expect(response.body).to.have.property("status", true);
        done(error);
      });
  });

});

import express from 'express';
import userRoutes from "./usersRoutes";
import todoRoutes from './todoRoutes';

const router = express.Router();

router.use('/user', userRoutes);
router.use('/todo', todoRoutes);

export default router;

import { Request, Response } from "express";
import { comparePassword, encryptPassword, generateToken } from '../../../utils'
import { createUser, deleteUserByEmail, findUserByEmail, findUserById, getAllRegisteredUsers, updateUserByEmail } from "../repository/userRepository";

const registerUser = async (req: Request, res: Response)=>{
    const {name, email, password} = req.body;
    const user = await findUserByEmail(email);
    if(user) return res.json({status: false, message: "User already exist."});
    const hashedPassword = await encryptPassword(password);
    const newUser = {
        name: name,
        email: email,
        password: hashedPassword
    };
    const newCreatedUser = await createUser(newUser)
    res.json({status: true, message: newCreatedUser.name + " registered."});
}

const getAllUsers = async (req: Request, res: Response)=>{
    res.json({status: true, message: await getAllRegisteredUsers()});
}

const checkUser = async(req: Request, res: Response)=>{
    const user = await findUserById((req as any).userId)
    if(!user) return res.json({status: false, message: "User not found!"});
    res.json({status: true, message: user});
}

const loginUser = async (req: Request, res: Response)=>{
    const {email, password} = req.body;
    const user = await findUserByEmail(email);
    if(!user) return res.json({status: false, message: "Invalid credentials"});
    const passwordMatches = await comparePassword(password, user.password);
    if(!passwordMatches) return res.json({status: false, message: "Invalid credentials"});
    const token = generateToken(user.id);
    res.json({status: true, message: {token}});
}

const deleteUser = async (req: Request, res: Response)=>{
    const {email} = req.body;
    const user = await findUserByEmail(email);
    if(!user) return res.json({status: false, message: "User doesn't exist."});
    const { deletedCount } = await deleteUserByEmail(email);
    if(deletedCount < 1) res.json({status: false, message: "Failed to delete User"});
    else res.json({status: true, message: "Deleted"});
}

const updateUser = async (req: Request, res: Response)=>{
    const {name, email} = req.body;
    const user = await findUserByEmail(email);
    if(!user) return res.json({status: false, message: "User doesn't exist."});
    const updatedUser = await updateUserByEmail(email, {name});
    if(updatedUser.modifiedCount > 0) res.json({status: true, message: "User updated successfully"});
    else res.json({status: false, message: "Failed to update User"});
}

export {
    registerUser,
    getAllUsers,
    loginUser,
    deleteUser,
    updateUser,
    checkUser,
}

import jwt from 'jsonwebtoken'
import { encryptPassword, comparePassword } from './passwordUtils';

const JWT_KEY = process.env.JWT_KEY || 'SECRET';

const generateToken = (userId: string): string => {
    const token = jwt.sign({userId}, JWT_KEY, {expiresIn: '1d'});
    return token;
}

export {
    generateToken,
    encryptPassword,
    comparePassword,
}

import bcrypt from 'bcrypt';

const encryptPassword = async (password: string) =>{
    return await bcrypt.hash(password, 10);
}

const comparePassword = async (password: string, hashedPassword: string) =>{
    return await bcrypt.compare(password, hashedPassword);
}

export {
    encryptPassword,
    comparePassword
}

